mapper.select:
select
from tb_graph where displayflag = '2' order by moneyreward desc
LIMIT #{offset},#{count}

select
from tb_graph where displayflag = '2' order by submissionnums desc
LIMIT #{offset},#{count}

select
from tb_graph where displayflag = '2' order by addtime desc
LIMIT #{offset},#{count}

mapper.update:
update tb_graph
set ticknums = #{ticknums}
where uid = #{uid}

update tb_graph
set favcount = #{favcount}
where uid = #{uid}

update tb_graph
set state =#{state},
  bidsubmission =#{bidsubmission},
  graphendtime =#{graphendtime}
where uid = #{uid}

update tb_graph
set state =#{state},
bidsubmission =#{submissionid},
graphendtime =#{graphendtime}
where uid = #{graphid}

mapper.select:
select
from tb_graph where state = '4' and bidsubmission is not null order by addtime desc
LIMIT #{offset},#{count}
